@php
// Helper function for active classes
function menuClass($routes) {
return request()->routeIs($routes)
? 'bg-black text-white shadow-[0_6px_12px_rgba(253,199,72,0.29)]'
: 'bg-transparent text-muted-foreground hover:bg-muted';
}
function svgClass($routes) {
return request()->routeIs($routes)
? 'text-white' // Color when the menu is active
: 'text-black'; // Color when the menu is inactive
}
